/* --------------------------
:: 0.0 Custom Normalize CSS Start
-------------------------- */
* {
  margin: 0;
  padding: 0;
}
body {
  font-family: "Montserrat", sans-serif;
  color: #2222;
  background-color: #ffffff;
  font-size: 14px;
}
body > * {
  overflow-x: hidden;
}
h1,
h2,
h3,
h4,
h5,
h6 {
  font-weight: 700;
  color: #232323;
  line-height: 1.2;
}
ul,
ol {
  margin: 0;
  padding: 0;
}
a,
a:hover {
  color: #ffffff;
}
a,
a:hover,
a:focus,
a:active,
button,
button:hover {
  text-decoration: none;
  touch-action: manipulation;
  -webkit-transition-duration: 500ms;
  -o-transition-duration: 500ms;
  transition-duration: 500ms;
}
li {
  list-style: none;
}
p,
label {
  font-size: 14px;
  color: #232323;
}

/* --------------------------
:: 1.0 Navbar CSS Start
-------------------------- */
.navbar {
  padding: 0 0.3rem;
  flex-wrap: nowrap;
}
.navbar-brand > img {
  width: 120px;
  height: auto;
}
.navbar p {
  color: #ffffff;
}
.avatar {
  position: relative;
  width: 2rem;
  height: 2rem;
  font-weight: 400;
  color: #6e7582;
  text-align: center;
  background: #f1f3f8 no-repeat center/cover;
  background-image: url(/e458e261787ea6c6a2452524e63c0386.png);
  border-radius: 50%;
}
.hamburger {
  display: flex;
  flex-direction: column;
  height: 20px;
  justify-content: space-between;
  position: relative;
}
.hamburger input {
  position: absolute;
  width: 40px;
  height: 28px;
  left: -5px;
  top: -3px;
  opacity: 0;
  cursor: pointer;
  z-index: 5;
}
.hamburger span {
  display: block;
  width: 28px;
  height: 3px;
  background-color: #ffffff;
  border-radius: 3px;
  transition: all 500ms;
}
.hamburger span:nth-child(2) {
  transform-origin: 0 0;
}
.hamburger span:nth-child(4) {
  transform-origin: 0 100%;
}
.hamburger input:checked ~ span:nth-child(2) {
  background-color: #f1f3f8;
  transform: rotate(45deg) translate(-1px, -1px);
}
.hamburger input:checked ~ span:nth-child(3) {
  opacity: 0;
  transform: scale(0);
}
.hamburger input:checked ~ span:nth-child(4) {
  background-color: #f1f3f8;
  transform: rotate(-45deg) translate(-1px, 0);
}
nav ul {
  position: fixed;
  right: 0;
  top: 0;
  height: 100vh;
  flex-direction: column !important;
  background-color: #343a40;
  width: 100%;
  transform: translateX(100%);
  transition: all 1s;
  opacity: 0;
  z-index: -1;
  padding-top: 60px;
}
nav ul.slide {
  transform: translateX(0);
  opacity: 1;
}
nav ul .nav-item {
  padding: 15px;
  border-bottom: thin solid #6e7582;
}

/* --------------------------
:: 2.0 Main CSS Start
-------------------------- */
.main {
  display: flex;
  background: url(/6540aed49f395a33b1e4477c41da0c95.png) no-repeat;
  background-size: cover;
  background-position: 33%;
  background-size: cover;
  padding-top: 70px;
  color: #fff;
  min-height: 100vh;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  z-index: -2;
}
.main .logo {
  width: 50px;
}
/* --------------------------
:: 2.1 Score Board CSS Start
-------------------------- */
.score-board {
  display: flex;
  padding-left: 30px;
  justify-content: space-between;
}
.score-board img {
  width: 40px;
}
.left-player {
  margin-left: -60px;
}
.left-player,
.right-player {
  display: flex;
  align-items: center;
}
.player-avatar {
  position: relative;
}
.player-avatar::before {
  content: "";
  background: url(/e458e261787ea6c6a2452524e63c0386.png);
  background-size: 30px;
  width: 30px;
  height: 30px;
  display: block;
  position: absolute;
  border-radius: 50%;
  top: 4;
  left: 5;
}
.player-avatar::after {
  content: "";
  background: url(/e458e261787ea6c6a2452524e63c0386.png);
  background-size: 30px;
  width: 30px;
  height: 30px;
  display: block;
  position: absolute;
  border-radius: 50%;
  bottom: 4;
  left: 5;
}
.point > p {
  font-weight: bold;
}
.score {
  padding-bottom: 12px;
}
.score .point {
  background-color: #ffc107;
  width: 20px;
  height: 20px;
  border-radius: 50%;
  margin-bottom: 5px;
}
.left-player .score h4 {
  color: #fff;
  margin-left: 10px;
}
.left-player .score .point {
  margin-left: auto;
  text-align: center;
}
span.line {
  background-color: #fff;
  width: 8px;
  height: 5px;
  margin: auto;
}
.right-player .score h4 {
  color: #fff;
  margin-right: 10px;
}
.right-player .score .point {
  margin-right: auto;
  text-align: center;
}
/* --------------------------
:: 2.2 Play CSS Start
-------------------------- */
.play-board {
  margin: 20px auto 0;
}
.play-board .card-basic {
  width: 60px;
}
#player {
  display: flex;
  flex-direction: column;
  align-items: center;
}
#player.left {
  margin-right: 15px;
}
#player.top {
  margin-bottom: 15px;
}
#player.right {
  margin-left: 15px;
}
#player.bottom {
  margin-top: 15px;
}
#player.left .name,
#player.right .name,
#player.bottom .name {
  margin-top: 5px;
}
#player.top .name {
  margin-bottom: 5px;
}
#player .name {
  background-color: black;
  opacity: 0.7;
  border-radius: 5px;
}
#player .name p {
  color: #fff;
  font-size: 12px;
  padding: 2px;
  margin: 0;
}
.selected {
  position: relative;
}
.selected-player {
  position: absolute;
  display: block;
  top: -25;
  right: 48;
}
.selected-player #crown {
  position: absolute;
  top: 0;
  left: 10;
  width: 30px;
}
.selected-player #circle {
  position: absolute;
  top: 28;
  left: 13;
  width: 24px;
}
/* --------------------------
:: 2.3 Card Preview CSS Start
-------------------------- */
.card-preview {
  margin-top: -60px;
}
.card-preview .card-basic {
  width: 30px;
}
/* --------------------------
:: 2.4 Card Deck CSS Start
-------------------------- */
.card-deck {
  margin-top: 20px;
  padding-bottom: 5px;
  position: relative;
}
.card-deck .card-basic {
  width: 50px;
}

